(497, 1, '2022-05-11 10:08:24', '2022-05-11 10:08:24', '<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Cosas de casa y cocina.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Olis / alcohol d’herba.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Talleres de Radiestesia, Energioterapia, Pan Artesanal con harinas especiales, Masas de pizza por encargo, Cremas de Calendula y Ruda, Aceite de Magnesio, Sahumerios, Flores comestibles de calendula, Hierbas de laurel, hierba luisa y lavanda, Sal de agua de Mar deshidratada.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Cosmetica natural<br>Roba i joies de segona ma</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:gallery {\"linkTo\":\"none\",\"align\":\"wide\"} -->\n<figure class=\"wp-block-gallery alignwide has-nested-images columns-default is-cropped\"><!-- wp:image {\"id\":494,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://maresme.xarxasobirana.com/wp-content/uploads/sites/2/2022/05/photo_2022-05-11_12-01-17.jpg\" alt=\"\" class=\"wp-image-494\"/><figcaption>Pesto d\'herbes de l\'hort</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":495,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://maresme.xarxasobirana.com/wp-content/uploads/sites/2/2022/05/photo_2022-05-11_12-03-08.jpg\" alt=\"\" class=\"wp-image-495\"/><figcaption>Colonia de romaní, lavanda, salvia</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":496,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://maresme.xarxasobirana.com/wp-content/uploads/sites/2/2022/05/photo_2022-05-11_12-03-13.jpg\" alt=\"\" class=\"wp-image-496\"/><figcaption>Crema corporal amb avena, lavanda, oliva, germe, blat</figcaption></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Ous, articles de segona ma, infusions i crispetes.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Encens, ous i alguna cosa més.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Decoracion de pintura sobre paret, restauracio de mobles, quadres…</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Oferim ungüents elaborats de manera artesanal, oleats de plantes per via interna i/O externa, massatges concients a 4mans, acompanyem processos de salut amb moltes eines diferent: amb medecines de la terra, PNL, dejú, dieta, exercici, meditació…</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Cremes i olis naturals i bijuteria</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Sabons artesanals i cosetes de ganxet</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Quarços, flors de Bach</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Beguda de coco casolana. Servei de cafeteria (cafè i infusions). Productes de 2a mà. Servei com administrativa. Servei de cura i atenció als nens. Servei com dietista.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:image {\"id\":414,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"https://maresme.xarxasobirana.com/wp-content/uploads/sites/2/2022/05/1651173214598-Georgina-web.jpg\" alt=\"\" class=\"wp-image-414\"/><figcaption>Servei de cafeteria i molt més!</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Astrología. Coaching. Fitoterapia. Aromaterapia. Sanación Akashica / Cuántica. Reiki. Formación en Coaching, Reiki.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Portaré llibres d\'infantils i coses boniques.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Alfajores argentinos. Quixes. Objetos y ropa de segunda mano.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Pastissos, Plantes, Sabó per la roba.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Terapia Floral de nova generació, Terapia Estructural, Sanació i tractaments a distancia. Neteja d\'ànimes, Sabons de ruda.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":436,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://maresme.xarxasobirana.com/wp-content/uploads/sites/2/2022/05/photo_2022-05-03_07-46-53-768x1024.jpg\" alt=\"\" class=\"wp-image-436\"/><figcaption>Terapia presencial i online</figcaption></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">El Pont cap al país Juguiner: Espai infantil de joc lliure per infants de 3 a 7 anys (edats aproximades).</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Obert durant tot el mercat i d\'entrada i sortida lliure a elecció de cada infant i/o familia. Supervisió i acompanyament durant l\'activitat (els infants es poden quedar amb mi l\'estona que s\'acordi).</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">El servei està sostingut econòmicament en part per la organització del mercat i en part per les donacions de mares, pares i participants del mercat que vulguin que existeixi.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":444,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"https://maresme.xarxasobirana.com/wp-content/uploads/sites/2/2022/05/photo_2022-05-03_11-18-18-OLGA-PONT-768x1024.jpg\" alt=\"\" class=\"wp-image-444\"/><figcaption>El Pont cap al país Juguiner</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Diferents objectes de 2a mà tipus bisutería, lámpara de sal, pots per plantar, etc.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Verdures</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Cervesa artesana i col.laboració pel que calgui :)</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Cafe de chiapas (paquets 250g). Llibres per encàrreg i molta experiència amb la Moneda Lliure Ğ1</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"secondary\"} -->\n<p class=\"has-secondary-background-color has-background\">Masatges reequilibri, arracades plomes salvatges, champú plantes d\'alta muntanya, hidrolats Romaní camomila real, sauco d\'alta muntanya, clauers artesanals G1, informació sobre bioconstrucció.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">Verdura eco y pan de masa madre en horno de leña.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:paragraph {\"backgroundColor\":\"tertiary\"} -->\n<p class=\"has-tertiary-background-color has-background\">... <a href=\"http://maresme.xarxasobirana.com/inscripcions/\" data-type=\"page\" data-id=\"52\">posa aquí la teva oferta</a> ...</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Aquesta secció està reservada per a la promoció dels nostres paradistes i els seus productes i/o serveis. Si has fet la inscripció i no montes parada també pots estar present aquí per a promocionar els teus serveis. Fes la teva <a href=\"https://maresme.xarxasobirana.com/inscripcions/\" data-type=\"page\" data-id=\"52\">inscripció</a> i publicarem el que tu ofereixes aquí. També ens pots enviar fotos per <a href=\"mailto:maresme@redsoberana.com\">email</a>.</p>\n<!-- /wp:paragraph -->', 'Paradistes', '', 'inherit', 'closed', 'closed', '', '50-revision-v1', '', '', '2022-05-11 10:08:24', '2022-05-11 10:08:24', '', 50, 'https://maresme.xarxasobirana.com/?p=497', 0, 'revision', '', 0),
(498, 1, '2022-05-15 18:16:52', '2022-05-15 18:16:52', '<!-- wp:heading {\"level\":1,\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"700\"}},\"fontSize\":\"x-large\"} -->\n<h1 class=\"has-x-large-font-size\" style=\"font-style:normal;font-weight:700\">Segon Mercat d\'Intercanvi del Maresme</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1} -->\n<h1>Montgat - Espai Amrta</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>Diumenge dia 15 de maig de 2.022</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>C/Sant Antoni Maria Claret, 146-176. Montgat</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:group -->\n<div class=\"wp-block-group\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"100%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:100%\"><!-- wp:gallery {\"columns\":4,\"linkTo\":\"none\"} -->\n<figure class=\"wp-block-gallery has-nested-images columns-4 is-cropped\"><!-- wp:image {\"id\":155,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://maresme.xarxasobirana.com/wp-content/uploads/sites/2/2022/04/photo_2022-03-17-10.51.17.jpeg\" alt=\"\" class=\"wp-image-155\"/></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:separator {\"className\":\"is-style-dots\"} -->\n<hr class=\"wp-block-separator is-style-dots\"/>\n<!-- /wp:separator -->', 'Formulari d\'inscripció', '', 'inherit', 'closed', 'closed', '', '52-revision-v1', '', '', '2022-05-15 18:16:52', '2022-05-15 18:16:52', '', 52, 'https://maresme.xarxasobirana.com/?p=498', 0, 'revision', '', 0);
